$(window).load(function(){var a=[$('a[href="#home"]'),$('a[href="#services"]'),$('a[href="#portfolio"]')];$.each(a,function(){$(this).click(function(d){d.preventDefault();$(this).blur();var b=this.getAttribute("href");if(b=="#home"){$("html, body, document, window").animate({scrollTop:0},800)}else{var c=$(b);$("html, body, document, window").animate({scrollTop:c.offset().top},500)}})});$(document).scroll(function(){if($(this).scrollTop()>350){$(".backToTop").fadeIn(200)}else{$(".backToTop").fadeOut(200)}});$(".backToTop").click(function(){$("html, body, window, document").animate({scrollTop:0},1000)})});